  xhr.open("GET", PdfFile, true);
  xhr.responseType = "arraybuffer";

  xhr.onreadystatechange = function(){
  if (xhr.readyState === 4){
    if (((xhr.status === 200) || (xhr.status == 0)) && (xhr.response)){
      xhr.onloadend = function() {

