  }
}
xhr.send()

}

function PdfView(props) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {

    asyncCall()
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Grid 
    container 
    justify="center" 
    alignItems="center" 
    className="mainGrid"
    >

        <Grid item>
            <h1 className="heading">Preview your data on form</h1>
        </Grid>

        <Grid 
            container 
            item 
            xs={12} 
            className="root"
            >
                <div>
      <Button 
      variant="contained"
        color="secondary" 
        size="large" 
        onClick={handleOpen}
      >
          Preview  
      </Button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
        <Paper className={classes.paper}>
        <iframe id="test" style={{boxSsizing: 'border-box', width: '100%', height: '100%'}} ></iframe>
            <div id="annotation-layer"></div>

            <Box display="flex"  justifyContent='center'>
                  <Button 
                      variant="contained" 
                      color="primary" 
                      size="medium" 
                      onClick={handleClose}
                  >
                          Close  
                  </Button>
            </Box>
        </Paper>
        </Fade>
      </Modal>
    </div>
            </Grid>


        <Navigation prev="/step1/ms" next="/" />
  </Grid>

  );
}

function mapStateToProps(state){
  return state;
}

export default connect(mapStateToProps)(PdfView);
