import {
    NAME_ACTION,
    SSN_ACTION,
    ADDRESS_ACTION,
    MS_ACTION,
    STEP1_ACTION
} from '../actions/formActions'


const formState = {
    step1: {
        name:{
            firstName:null,
            middleName:null,
            lastName:null
        },
        address:{
            street:null,
            city:null,
            state:null,
            zip:null
        },
        maritalStatus:null
    },

    step2:{
        multiCheckBox:null
    },

    step3:{
        qualifyingChildren:null,
        dependents:null,
        totalDependents:null
    },

    step4:{
        otherIncome:null,
        deductions:null,
        extraWithholding:null
    },

    step5:{
        signature:null,
        date:null
    }
}


export default function formReducer(state = formState, action){
    switch(action.type){
        case NAME_ACTION:
            const {firstName, middleName, lastName} = action;
            return {
                ...state,
                ['name']:{
                    firstName: firstName,
                    middleName: middleName == 'undefined' ? '': middleName,
                    lastName: lastName
                }
            }
    }

    return state;
}
