import {
    NAME_ACTION,
    SSN_ACTION,
    ADDRESS_ACTION,
    MS_ACTION,
    STEP1_ACTION
} from '../actions/formActions'

import { combineReducers } from 'redux';


const formState = {
    step1: {
        name:{
            firstName:'',
            middleName:'',
            lastName:''
        },
        address:{
            street:'',
            city:'',
            state:'',
            zip:''
        },
        maritalStatus:''
    },

    step2:{
        multiCheckBox:''
    },

    step3:{
        qualifyingChildren:'',
        dependents:'',
        totalDependents:''
    },

    step4:{
        otherIncome:'',
        deductions:'',
        extraWithholding:''
    },

    step5:{
        signature:'',
        date:''
    }
}

const screenState = {
    screen: 0
}


function formReducer(state = formState, action){
    switch(action.type){
        case NAME_ACTION:
            const {firstName, middleName, lastName} = action;
            return {
                ...state,
                ['step1']:{
                    ...state['step1'],
                    ['name']:{
                        firstName: firstName,
                        middleName: middleName == 'undefined' ? '': middleName,
                        lastName: lastName
                    }
                }
            }
    }

    return state;
}


function screenReducer(state=screenState, action){

    return state;
}


export default combineReducers({
    screenReducer,
    formReducer
})
