import React, { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import Navigation from '../components/navigation'
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { connect } from 'react-redux';

import '../App.css';

function MaritalStatusComponent(props){

    const [ms, setMs] = useState(props.formReducer.step1.maritalStatus);
    const msStatus = {
        si: 'Single or Married filing separately',
        ma: 'Married filing jointly (or Qualifying widow(er))',
        hh: 'Head of household '
    }

    return (
        <Grid 
        container 
        justify="center" 
        alignItems="center" 
        className="mainGrid"
        >

            <Grid item>
                <h1 className="heading">What's your Marital Status?</h1>
                {
                    Object
                    .keys(msStatus)
                    .map(ms => (
                        <Grid 
                        container 
                        item 
                        key={ms} 
                        xs={12} 
                        className="root"
                        >
                            <FormControlLabel
                                control={
                                <Checkbox
                                    name="checkedB"
                                    color="primary"
                                />
                                }
                                label={msStatus[ms]}
                            />
                </Grid>
                    ))
                }

            </Grid>

            <Navigation prev="/step1/name" next="/" />
      </Grid>
    )

}

function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps)(MaritalStatusComponent);
