import React from 'react'
import Grid from '@material-ui/core/Grid';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import Navigation from '../components/navigation'
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import { STATES } from '../data/states'
import {addressData} from '../actions/formActions'
import '../App.css';


class AddressComponent extends React.Component{

    state = this.props.formReducer.step1.address

    handleAddr = (ev, name) => {
        this.setState({
            ...this.state,
            [name]: ev.target.value
        })
    }

    render(){
        return(
            <Grid 
            container 
            justify="center" 
            alignItems="center"
            className="mainGrid"
            >
                <Grid item>
                    <h1 className="heading">Address Information</h1>
                </Grid>
                <Grid 
                        container 
                        item 
                        key="street" 
                        xs={12} 
                        className="root"
                        >
                            <TextField 
                                className="root"
                                label="street" 
                                variant="outlined" 
                                value={this.state.street} 
                                onChange={ev =>  this.handleAddr(ev, "street")}
                            />
                </Grid>

                <Grid 
                        container 
                        item 
                        key="city" 
                        xs={12} 
                        className="root"
                        >
                            <TextField 
                                className="root"
                                label="city" 
                                variant="outlined" 
                                value={this.state.city} 
                                onChange={ev =>  this.handleAddr(ev, "city")}
                            />
                </Grid>             
                <Grid 
                container 
                item 
                key="state" 
                xs={12} 
                className="root"
                >
                    <FormControl variant="filled" className="root">
                        <InputLabel id="demo-simple-select-filled-label">Age</InputLabel>
                            <Select
                            labelId="demo-simple-select-filled-label"
                            value={this.state.state}
                            onChange={ev =>  this.handleAddr(ev, "state")}
                            >
                            <MenuItem value="">
                                <em>None</em>
                            </MenuItem>
                            {
                                STATES.map(state => <MenuItem key={state} value={state}>{state}</MenuItem>)
                            }
                            </Select>
                    </FormControl>
                </Grid>
                <Grid 
                        container 
                        item 
                        key="zip" 
                        xs={12} 
                        className="root"
                        >
                            <TextField 
                                className="root"
                                label="zip" 
                                variant="outlined" 
                                value={this.state.zip} 
                                onChange={ev =>  this.handleAddr(ev, "zip")}
                />
                </Grid>

                <Navigation prev="/step1/ssn" next="/"  action={addressData(this.state)}/>



            </Grid>
        )
    }
}

function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps)(AddressComponent);
