import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import {isString} from '../validators/inputValidations'
import { connect } from 'react-redux';
import {nameData} from '../actions/formActions'
import Padding from '../components/padding'
import Navigation from '../components/navigation'

const useStyles = makeStyles({
    container: { 
      backgroundColor: '#ccf0e1', 
      height: '80vh', 
      marginTop:'10vh' 
    },
    root: {
      width: '100%'
    },
    heading:{
      color: '#084177'
    },
});


const validateText = (ev, type, name, setName) => {
    let str = ev.target.value.replace(name[type], '');
    if(isString(str))
        setName({
            ...name,
            [type]: ev.target.value
        });
}

/**************************
 * Update the Redux State *
 **************************/
const updateState = (name, props) => {
    console.log(props);
    const NameDataAction = nameData(name);
    props.dispatch(NameDataAction);
}

function NameComponent(props){
    const classes = useStyles();
    const [name, setName] = useState({firstName:'', middleName:'', lastName: ''});
    return(
        <form noValidate autoComplete="off" style={{height: "100%"}}>
                <Grid container justify="center" direction="column" alignItems="center" spacing={5}>

                  <Grid item xs={12}>
                    <h1 className={classes.heading}>Let's get your Name</h1>
                  </Grid>

                  <Padding length="1" name="padding-1"/>
                  {
                      Object.keys(name).map(nameObj => (
                        <Grid key={nameObj} item xs={5} className={classes.root}>
                            <TextField 
                                className={classes.root} 
                                label={nameObj.charAt(0).toUpperCase() + nameObj.substring(1,nameObj.length)} 
                                variant="outlined" 
                                value={name[nameObj]} 
                                onChange={(ev) => validateText(ev,nameObj,name,setName)}
                            />
                        </Grid>
                      ))

                  }
                 <Padding length="3" name="padding-2"/>
                 <Navigation prev="/" next="/step1/ssn" name={name} dispatch={props.dispatch} updateComponent={updateState}/>

                </Grid>
              </form>
    );
}


function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps)(NameComponent);
