import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import {isString} from '../validators/inputValidations'

const useStyles = makeStyles({
    container: { 
      backgroundColor: '#ccf0e1', 
      height: '80vh', 
      marginTop:'10vh' 
    },
    root: {
      width: '100%'
    },
    heading:{
      color: '#084177'
    },
  });


const validateText = (ev, type, name, setName) => {
    switch(type){
        case 'fn':
            varstr = ev.target.value.replace(name.fn, '');
            if(isString(str))
                setName({
                    ...name,
                    fn: ev.target.value
                });
        case 'mn':
            str = ev.target.value.replace(name.mn, '');
            if(isString(str))
                setName({
                    ...name,
                    mn: ev.target.value
                });
        case 'ln':
            str = ev.target.value.replace(name.ln, '');
            if(isString(str))
             setName({
                    ...name,
                    ln: ev.target.value
            });
    }


}

export default function NameComponent(){
    const classes = useStyles();
    const [name, setName] = useState({fn:'', mn:'', ln: ''});
    return(
        <form noValidate autoComplete="off" style={{height: "100%"}}>
                <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
                  <Grid item xs={12}>
                    <h2 className={classes.heading}>Let's get your Name</h2>
                  </Grid>
                  <Grid item xs={4} className={classes.root}>
                     <TextField className={classes.root} label="First Name" variant="outlined" value={name.fn} onChange={(ev) => validateText(ev,'fn',name,setName)}/>
                  </Grid>
                  <Grid item xs={4} className={classes.root}>
                     <TextField className={classes.root}  label="Middle Name" variant="outlined" value={name.mn} onChange={(ev) => validateText(ev,'mn',name,setName)}/>
                  </Grid>
                  <Grid item xs={4} className={classes.root}>
                     <TextField className={classes.root}  label="Last Name" variant="outlined" value={name.ln} onChange={(ev) => validateText(ev,'ln',name,setName)}/>
                  </Grid>
                  <Grid item xs={4}>
                 </Grid>

                 <Grid item xs={4}>
                 </Grid>

                 <Grid item xs={4}>
                 </Grid>

                 <Grid item >
                    <Button variant="outlined" color="primary" size="large">
                              NEXT
                    </Button>
                 </Grid>

                </Grid>
              </form>
    );
}
