import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import {isString} from '../validators/inputValidations'
import { connect } from 'react-redux';


const useStyles = makeStyles({
    container: { 
      backgroundColor: '#ccf0e1', 
      height: '80vh', 
      marginTop:'10vh' 
    },
    root: {
      width: '100%'
    },
    heading:{
      color: '#084177'
    },
  });


const validateText = (ev, type, name, setName) => {
    let str = ev.target.value.replace(name[type], '');
    if(isString(str))
        setName({
            ...name,
            [type]: ev.target.value
        });
}

function NameComponent(props){
    console.log(props)
    const classes = useStyles();
    const [name, setName] = useState({firstName:'', middleName:'', lastName: ''});
    return(
        <form noValidate autoComplete="off" style={{height: "100%"}}>
                <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
                  <Grid item xs={12}>
                    <h1 className={classes.heading}>Let's get your Name</h1>
                  </Grid>
                  <Grid key="padding-0" item xs={4}></Grid>

                  {
                      Object.keys(name).map(nameObj => (
                        <Grid key={nameObj} item xs={5} className={classes.root}>
                            <TextField 
                                className={classes.root} 
                                label={nameObj.charAt(0).toUpperCase() + nameObj.substring(1,nameObj.length)} 
                                variant="outlined" 
                                value={name[nameObj]} 
                                onChange={(ev) => validateText(ev,nameObj,name,setName)}
                            />
                        </Grid>
                      ))

                  }

                  {
                      Array.from({length: 3}).map((x,i) => <Grid key={"padding"+(i+1)} item xs={4}></Grid>)
                  }

                 <Grid item >
                    <Button variant="outlined" color="primary" size="large">NEXT</Button>
                 </Grid>

                </Grid>
              </form>
    );
}

export default connect()(NameComponent);
