import Grid from '@material-ui/core/Grid';
import Navigation from '../components/navigation'
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import { connect } from 'react-redux';

import '../App.css';

function MaritalStatusComponent(props){

    const [ms, setMs] = useState(props.formReducer.step1.maritalStatus);
    const msStatus = {
        si: 'Single or Married filing separately',
        ma: 'Married filing jointly (or Qualifying widow(er))',
        hh: 'Head of household (Check only if you’re unmarried and pay more than half the costs of keeping up a home for yourself and a qualifying individual.) '
    }

    return (
        <Grid 
        container 
        justify="center" 
        alignItems="center" 
        className="mainGrid"
        >

            <Grid item>
                <h1 className="heading">What's your Marital Status?</h1>
            </Grid>

            <Grid 
                container 
                item 
                key="state" 
                xs={12} 
                className="root"
                >
                    <FormControl variant="filled" className="root">
                        <InputLabel >State</InputLabel>
                            <Select
                            value={this.state.state}
                            onChange={ev =>  this.handleAddr(ev, "state")}
                            >
                            <MenuItem value="">
