                            {
                                STATES.map(state => <MenuItem key={state} value={state}>{state}</MenuItem>)
                            }
                            </Select>
                    </FormControl>
                </Grid>


            <Navigation prev="/step1/name" next="/" />
      </Grid>
    )

}

function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps)(MaritalStatusComponent);
