import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import {isString} from '../validators/inputValidations'

const useStyles = makeStyles({
    container: { 
      backgroundColor: '#ccf0e1', 
      height: '80vh', 
      marginTop:'10vh' 
    },
    root: {
      width: '100%'
    },
    heading:{
      color: '#084177'
    },
  });


const validateText = (ev, val, setVal) => {
    const str = ev.target.value.replace(val, '');
    if(isString(str))
        setVal(ev.target.value);

}

export default function NameComponent(){
    const classes = useStyles();
    const [fn, setFN] = useState('');
    return(
        <form noValidate autoComplete="off" style={{height: "100%"}}>
                <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
                  <Grid item xs={12}>
                    <h2 className={classes.heading}>Let's get your Name</h2>
                  </Grid>
                  <Grid item xs={4} className={classes.root}>
                     <TextField className={classes.root} label="First Name" variant="outlined" value={fn} onChange={(ev) => validateText(ev,fn,setFN)}/>
                  </Grid>
                  <Grid item xs={4} className={classes.root}>
                     <TextField className={classes.root}  label="Middle Name" variant="outlined" />
                  </Grid>
                  <Grid item xs={4} className={classes.root}>
                     <TextField className={classes.root}  label="Last Name" variant="outlined" />
                  </Grid>
                  <Grid item xs={4}>
                 </Grid>

                 <Grid item xs={4}>
                 </Grid>

                 <Grid item xs={4}>
                 </Grid>

                 <Grid item >
                    <Button variant="outlined" color="primary" size="large">
                              NEXT
                    </Button>
                 </Grid>

                </Grid>
              </form>
    );
}
