import {
    NAME_ACTION,
    SSN_ACTION,
    ADDRESS_ACTION,
    MS_ACTION,
    STEP1_ACTION
} from '../actions/formActions'


const formState = {
    step1: {
        name:{
            firstName,
            middleName,
            lastName
        },
        address:{
            street,
            city,
            state,
            zip
        },
        maritalStatus
    },

    step2:{
        multiCheckBox
    },

    step3:{
        qualifyingChildren,
        dependents,
        totalDependents
    },

    step4:{
        otherIncome,
        deductions,
        extraWithholding
    },

    step5:{
        signature,
        date
    }
}


export default function formReducer(state = formState, action){
    switch(action.type){
        case NAME_ACTION:
            const {firstName, middleName, lastName} = action;
            return {
                ...state,
                ['name']:{
                    firstName: firstName,
                    middleName: middleName == 'undefined' ? '': middleName,
                    lastName: lastName
                }
            }
    }
}
