import Grid from '@material-ui/core/Grid';
import Padding from '../components/padding'
import Navigation from '../components/navigation'
import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import {isNumber} from '../validators/inputValidations'


const validateNumberField = (ev, ssn, setSsn) => {
    let str = ev.target.value.replace(ssn, '');
    let ssnInp = ev.target.value;
    console.log(str);
    if(isNumber(str) && ssn.length <10){
        if(ssnInp.length == 3)
            ssnInp +='-'
        if(ssnInp.length == 6)
            ssnInp += '-'
        setSsn(ssnInp);
    }
}

export default function SSNComponent(props){
    const [ssn, setSsn] = useState('');

    return(

        <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
            <Grid item xs={12}>
                <h1 style={{color: '#084177'}}>Enter Your 9 Digit Social Security Number</h1>
            </Grid>

            <Padding length="4" />

            <Grid key="ssn" item xs={5} style={{width: '100%'}}>
                <TextField 
                    style={{width: '100%'}} 
                    label="SSN" 
                    variant="outlined" 
                    value={ssn} 
                    onChange={(ev) => validateNumberField(ev, ssn, setSsn)}
                />
            </Grid>

            <Padding length="5" />


            <Navigation next="/step1/ssn"  />
      </Grid>

    )

}
