import React from 'react'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import {Link} from 'react-router-dom'
import {nameData} from '../actions/formActions'


/**************************
 * Update the Redux State *
 **************************/
const updateState = (props) => {
    console.log(props)
    if(props.dispatch){
        const NameDataAction = nameData(props.name);
        props.dispatch(NameDataAction);
    }
}

export default function Navigation(props){
    return (
        <Grid item xs={12} container styles={{width: '100%'}} justify="center">

            {
                props.prev 
                    && 
                <Link to={props.prev} style={{margin: '1%'}}>
                    <Button variant="outlined" color="primary" size="large" style={{width: '100px'}}>
                        PREVIOUS    
                    </Button>
                 </Link>    
            }          

            {
                props.next
                  &&
                <Link to={props.next} style={{margin: '1%'}}>
                  <Button 
                      variant="outlined" 
                      color="primary" 
                      size="large" 
                      style={{width: '100px'}}
                      onClick={() => updateState(props)}>
                          NEXT  
                  </Button>
              </Link>  
            }    
        </Grid>
    )
}
