import React from 'react'
import Grid from '@material-ui/core/Grid';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import Navigation from '../components/navigation'
import '../App.css';

class AddressComponent extends React.Component{

    state = this.props.formReducer.step1.address

    handleAddr = (ev, name) => {
        this.setState({
            ...this.state,
            [name]: ev.target.value
        })
    }

    render(){
        console.log(this.state)
        return(
            <Grid 
            container 
            justify="center" 
            alignItems="center"
            className="mainGrid"
            >
                <Grid item>
                    <h1 className="heading">Address Information</h1>
                </Grid>


                {
                      Object.keys(this.state).map(addr => (
                        <Grid 
                        container 
                        item 
                        key={addr}  
                        xs={12} 
                        className="root"
                        >
                            <TextField 
                                className="root"
                                label={addr.charAt(0).toUpperCase() + addr.substring(1,addr.length)} 
                                variant="outlined" 
                                value={this.state.addr} 
                                onChange={ev =>  this.handleAddr(ev, addr)}
                            />
                        </Grid>
                      ))

                  }

                <Navigation prev="/step1/ssn" next="/"  />



            </Grid>
        )
    }
}

function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps)(AddressComponent);
