import Grid from '@material-ui/core/Grid';
import Padding from '../components/padding'
import Navigation from '../components/navigation'
import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import { connect } from 'react-redux';
import {ssnData} from '../actions'

const format = (str) => {

    if(str.length>=4 && str.charAt(3) !== '-')
        str = str.substring(0,3)+"-"+str.substring(3,str.length)
    if(str.length>=8 && str.charAt(7) !== '-')
        str = str.substring(0,7)+"-"+str.substring(7,str.length)

    return str;
}

const validateNumberField = (ev, ssn, setSsn) => {
    ssn = ssn.split('-').join('');
    let ssnInp = ev.target.value.split('-').join('');
    let entering = ssn.length<ssnInp.length? true: false;

    if(ssnInp.length < 10){
        if(entering){
            let input = ssnInp.replace(ssn, '');
            if(!isNaN(input)){
                ssnInp = format(ssnInp);
                setSsn(ssnInp);
            }
            return;
        }
        setSsn(format(ev.target.value));
    }
}

/**************************
 * Update the Redux State *
 **************************/
const updateState = (ssn, props) => {
    const SSNAction = ssnData(ssn);
    props.dispatch(SSNAction);
}

function SSNComponent(props){
    const [ssn, setSsn] = useState('');
    return(

        <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
            <Grid item xs={12}>
                <h1 style={{color: '#084177'}}>Enter Your 9 Digit Social Security Number</h1>
            </Grid>

            <Padding length="4" />

            <Grid key="ssn" item xs={5} style={{width: '100%'}}>
                <TextField 
                    style={{width: '100%'}} 
                    label="SSN" 
                    variant="outlined" 
                    value={ssn} 
                    onChange={(ev) => validateNumberField(ev, ssn, setSsn)}
                />
            </Grid>

            <Padding length="5" />

            <Navigation prev="/step1/name" next="/step1/address"  dispatch={props.dispatch}/>
      </Grid>

    )

}

function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps)(SSNComponent);
