import React from 'react';
import './App.css';
import { connect } from 'react-redux';
import {nameData} from './actions/formActions'
import { Container } from '@material-ui/core';
import Typography from '@material-ui/core/Typography';
import NameComponent from './components/nameComponent'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';

class App extends React.Component{
  classes = {
    container: { 
      backgroundColor: '#ccf0e1', 
      height: '100%', 
      marginTop:'10%',
      padding: '5%'
    },
  };


  doThing = () => {
    this.props.dispatch(nameData({firstName:'Max', middleName: 'tonk', lastName:'Payne'}))
  }

  render(){
    console.log(this.props.screenReducer.screenState);
    return (
      <Container maxWidth="md">
        {
            <Typography component="div" style={this.classes.container} >

            {
            this.props.screenReducer.screenState === 0 &&  
                <Grid container justify="center" direction="column" alignItems="center" spacing={5}>

                    <h1>YOLO MANDAL</h1>
                    <Grid item >
                          <Button variant="outlined" color="primary" size="large">NEXT</Button>
                    </Grid>

                </Grid>
            }
            {
              this.props.screenReducer.screenState === 1 
                    &&
              <NameComponent />
            }
            </Typography>
        }
      </Container>
    );
  }
}

function mapStateToProps(state){
  return state;
}

export default connect(mapStateToProps)(App);
