import Grid from '@material-ui/core/Grid';
import Padding from '../components/padding'
import Navigation from '../components/navigation'
import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';


const format = (str) => {

    if(str.length>=3 && str.charAt(3) !== '-')
        str = str.substring(0,3)+"-"+str.substring(3,str.length)
    if(str.length>=7 && str.charAt(7) !== '-')
        str = str.substring(0,7)+"-"+str.substring(7,str.length)

    return str;
}

const validateNumberField = (ev, ssn, setSsn) => {
    ssn = ssn.split('-').join('');
    let ssnInp = ev.target.value.split('-').join('');
    let input = ssn.length<ssnInp.length? ssnInp.replace(ssn, ''): ssn.replace(ssnInp, '');
    console.log(input)
    console.log(ssnInp);
    if(ssnInp.length < 10){
        if(!isNaN(input)){
            ssnInp = format(ssnInp);
            setSsn(input == ''? ssnInp.substring(0,ssnInp.length-1):ssnInp);
            return;
        }
        setSsn(ssnInp);
    }
}

export default function SSNComponent(props){
    const [ssn, setSsn] = useState('');

    return(

        <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
            <Grid item xs={12}>
                <h1 style={{color: '#084177'}}>Enter Your 9 Digit Social Security Number</h1>
            </Grid>

            <Padding length="4" />

            <Grid key="ssn" item xs={5} style={{width: '100%'}}>
                <TextField 
                    style={{width: '100%'}} 
                    label="SSN" 
                    variant="outlined" 
                    value={ssn} 
                    onChange={(ev) => validateNumberField(ev, ssn, setSsn)}
                />
            </Grid>

            <Padding length="5" />


            <Navigation next="/step1/ssn"  />
      </Grid>

    )

}
