import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import {isString} from '../validators/inputValidations'
import { connect } from 'react-redux';
import {Link} from 'react-router-dom'
import {nameData} from '../actions/formActions'
import Padding from '../components/padding'


const useStyles = makeStyles({
    container: { 
      backgroundColor: '#ccf0e1', 
      height: '80vh', 
      marginTop:'10vh' 
    },
    root: {
      width: '100%'
    },
    heading:{
      color: '#084177'
    },
  });


const validateText = (ev, type, name, setName) => {
    let str = ev.target.value.replace(name[type], '');
    if(isString(str))
        setName({
            ...name,
            [type]: ev.target.value
        });
}

/**************************
 * Update the Redux State *
 **************************/
const updateState = (name, props) => {
    const NameDataAction = nameData(name);
    props.dispatch(NameDataAction);
}

function NameComponent(props){
    const classes = useStyles();
    const [name, setName] = useState({firstName:'', middleName:'', lastName: ''});
    return(
        <form noValidate autoComplete="off" style={{height: "100%"}}>
                <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
                  <Grid item xs={12}>
                    <h1 className={classes.heading}>Let's get your Name</h1>
                  </Grid>

                  <Grid key="padding-1" item xs={4}></Grid>

                  {
                      Object.keys(name).map(nameObj => (
                        <Grid key={nameObj} item xs={5} className={classes.root}>
                            <TextField 
                                className={classes.root} 
                                label={nameObj.charAt(0).toUpperCase() + nameObj.substring(1,nameObj.length)} 
                                variant="outlined" 
                                value={name[nameObj]} 
                                onChange={(ev) => validateText(ev,nameObj,name,setName)}
                            />
                        </Grid>
                      ))

                  }


                 <Padding length="3" name="padding-2"/>

                 <Grid item xs={12} container className={classes.root} justify="center">

                    <Link to="/" style={{margin: '1%'}}>
                        <Button variant="outlined" color="primary" size="large" style={{width: '100px'}}>
                                PREVIOUS    
                        </Button>
                    </Link>              

                    <Link to="/step1/name" style={{margin: '1%'}}>
                        <Button 
                            variant="outlined" 
                            color="primary" 
                            size="large" 
                            style={{width: '100px'}}
                            onClick={() => updateState(name, props)}>
                                NEXT  
                        </Button>
                    </Link>  
                 </Grid>

                </Grid>
              </form>
    );
}


function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps)(NameComponent);
