import Grid from '@material-ui/core/Grid';
import Padding from '../components/padding'
import Navigation from '../components/navigation'
import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';


const validateNumberField = (ev, ssn, setSsn) => {
    let ssnInp = ev.target.value;
    let input = ssn.length<ssnInp.length? ssnInp.replace(ssn, ''): ssn.replace(ssnInp, '');
    console.log(input)
    if(ssnInp.split('-').join('').length < 10){
        if(!isNaN(input)){
            if(ssnInp.length>=3 && ssnInp.charAt(3) !== '-')
                ssnInp = ssnInp.substring(0,3)+"-"+ssnInp.substring(3,ssnInp.length)
            if(ssnInp.length>=7 && ssnInp.charAt(7) !== '-')
                ssnInp = ssnInp.substring(0,7)+"-"+ssnInp.substring(7,ssnInp.length)
            setSsn(ssnInp);
            return;
        }
        setSsn(ssnInp);
    }
}

export default function SSNComponent(props){
    const [ssn, setSsn] = useState('');

    return(

        <Grid container justify="center" direction="column" alignItems="center" spacing={5}>
            <Grid item xs={12}>
                <h1 style={{color: '#084177'}}>Enter Your 9 Digit Social Security Number</h1>
            </Grid>

            <Padding length="4" />

            <Grid key="ssn" item xs={5} style={{width: '100%'}}>
                <TextField 
                    style={{width: '100%'}} 
                    label="SSN" 
                    variant="outlined" 
                    value={ssn} 
                    onChange={(ev) => validateNumberField(ev, ssn, setSsn)}
                />
            </Grid>

            <Padding length="5" />


            <Navigation next="/step1/ssn"  />
      </Grid>

    )

}
